import { Divider } from 'primereact/divider';
import { InputText } from 'primereact/inputtext';
import { Button } from 'primereact/button';
import react, { useState } from "react";
import "./data.js";
import { ReactDOM } from 'react';
import "./index.css";

function App(){
    
    
 

const [name,setName]=useState();
const [password,SetPassword]=useState();

   
    const submitHandler=(e)=>{
            e.preventDefault();
        
          const users = [
                {name: "mgmg",
                rollno:"mub01"
                },
                {name: "zawzaw",
                rollno:"mub02"
                },
                {name: "kgkg",
                rollno:"mub03"
                },
                {name: "chitchit",
                rollno:"mub04"
                },
                {name: "zinzin",
                rollno:"mub05"
                }
                ]   
                console.log(users)
          //const namee = "mgmg";
         //const pwd = "mub01";
         const getName =(arrayElement)=>{
            return arrayElement.name;}
            const getRollno =(arrayElement)=>{
                return arrayElement.rollno;}

      let result= users.filter(getName)
      let data = users.filter(getRollno)



                if(name === result && password === data ){
                    alert("hello world");

                }
                else{
                    alert("Error");
                }
            }

    
            
            
            

    return(
        
        <form onClick={submitHandler} className='login'>
            
               <div className="card">
            <div className="flex flex-column md:flex-row" >
                <div className="w-full md:w-5 flex flex-column align-items-center justify-content-center gap-3 py-5">
                    <div className="flex flex-wrap justify-content-center align-items-center gap-2">
                        <label className="w-6rem">Username</label>
                        <InputText id="username" type="text" value={name} onChange={(e)=> setName(e.target.value)} className="w-12rem" placeholder='Enter your name'  />
                    </div>
                    <div className="flex flex-wrap justify-content-center align-items-center gap-2">
                        <label className="w-6rem">Password</label>
                        <InputText id="password" type="password" value={password} onChange={(e)=>SetPassword(e.target.value)} className="w-12rem" placeholder='Enter your roll number' />
                        </div>
                    <Button label="Login" icon="pi pi-user" className="w-10rem mx-auto" id="login" ></Button>
                </div>
                
                
            </div>
           
        </div>
        </form>
     
       
     
    );
 

};



export default App;
