import { useState,useReducer } from "react";

const intialValue ={
  count: 0,
}
function reducer(state, action){
if (action.type === "INPUT_ADDED"){
const newPeople=[...state.people,action.payload];
return{ ...state, people:newPeople}
}
}

function App(){
    const [inputValue, setInputvalue]=useState("");
  
  
  const[state, dispatch] = useReducer(reducer, intialValue);
  function submitHandler(e){
    e.preventdefault();
    const newItem={id:Math.random().toString(),name:inputValue}
    dispatch({type:"INPUT_ADDED",payload:newItem})
  }
  
  
  return(
  
    <div>
      <h1>
       Form
      </h1>
     <form onSubmit={submitHandler}>
      <input type="text" value={inputValue} onChange={(e)=>setInputvalue(e.target.value)} placeholder="Enter your text"/>
      <button type="submit" >submit</button>
     </form>
    </div>
    
    
    
    
    
    );
    
  
  }

  




export default App;
